import storage from 'node-persist';

const initStorage = async () => {
  await storage.init();
};

const saveScore = async (score) => {
  let scores = await storage.getItem('scores');
  if (!scores) {
    scores = [];
  }
  scores.push(score);
  await storage.setItem('scores', scores);
};

const getScores = async () => {
  let scores = await storage.getItem('scores');
  if (!scores) {
    scores = [];
  }
  return scores;
};

export default {
  initStorage,
  saveScore,
  getScores,
};
